sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Use Ajax to get the Agency
    var rateRot = g_form.getValue('u_rate_description');
    var ga = new GlideAjax('ManualBilling');
    ga.addParam('sysparm_name', 'getRateInformation');
    ga.addParam('sysparm_revenue', rateRot);
    ga.getXMLAnswer(_getAnswer);
}

function _getAnswer(ans) {
    if (ans == 'failed') {
        alert('The lookup failed. Please create an incident.');
    } else {
        var ansList = ans.split(',');
        g_form.setValue('u_revenue_unit', ansList[0], ansList[1]);
        g_form.setValue('u_rate_code', ansList[2]);
        g_form.setValue('u_rate_value', parseFloat(ansList[3]).toFixed(2));        
        g_form.setValue('u_ru_approver', ansList[4]);

        var qty = g_form.getValue('u_quantity');

        if (!isNaN(qty) && qty != '') {
            qty = parseFloat(qty.replace(/[^0-9.+-]/g, ''));
            var amount = g_form.getValue('u_rate_value');
            amount = parseFloat(amount.replace('(', '-').replace(/[^0-9.+-]/g));
            var total = parseFloat(amount) * parseFloat(qty);
            g_form.setValue('u_total', parseFloat(total).toFixed(2));
        } else {
            g_form.setValue('u_total', parseFloat('0.00').toFixed(2));
        }
    }
}
