sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Use Ajax to get the Agency
	var rateCode = g_form.getValue('u_rate_description');
	alert('ratecode: ' + rateCode);
	var ga = new GlideAjax('ManualBilling');
	ga.addParam('sysparm_name','getRevenueCode');
	ga.addParam('sysparm_revenue',rateCode);
	ga.getXML(_getAnswer);
}

function _getAnswer(response) {
	var ans = response.responseXML.documentElement.getAttribute("answer");
	alert('ans: ' + ans);
	var ansList = ans.split(',');
	if (ans == 'failed') {
		alert('The lookup failed. Please create an incident.');
	} else {
		g_form.setValue('u_rate_code',ansList[0]);
		g_form.setValue('u_revenue_unit',ansList[1],ansList[2]);
		g_form.setValue('u_rate_value',parseFloat(ansList[3]).toFixed(2));
	}
}
