sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	//Use Ajax to get the check approvers
	var revenueUnit = g_form.getValue('u_revenue_unit');
	var ga = new GlideAjax('ManualBilling');
	ga.addParam('sysparm_name','checeRevenueApprovers');
	ga.addParam('sysparm_revenue',revenueUnit);
	ga.getXML(_getAnswer);
}

function _getAnswer(response) {
	var ans = response.responseXML.documentElement.getAttribute("answer");
	if (ans=='false') {		
		alert('There are no approvers. You will not be allowed to submit for approval until this revenue unit has approvers.');
	}
}
