sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '' || newValue === qty) {
		return;
	}
	//Calculate the Total.
	var multiplier = 1;
	var qty = (newValue.replace(/,/g,''));
	alert('qty: ' + qty);
	var amount = g_form.getValue('u_rate_value');
	alert('amount: ' + amount);
	var firstChar = amount.charAt(0);
	if (firstChar == '(') {
		multiplier = -1;
	}	
	amount = amount.replace(/[^0-9.+-,]/g,'');
	amount = parseFloat(amount);
	var total = parseFloat(amount) * parseFloat(qty) * parseFloat(multiplier);
	g_form.setValue('u_total',total.toFixed(2));
}
