var ManualBilling = Class.create();
ManualBilling.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	//type: 'ManualBilling',

	getAgency: function() {
		var userRec = this.getParameter('sysparm_user');

		var gr = new GlideRecord('sys_user');
		if (gr.get(userRec)) {
			var response = [];
			ans = gr.company + ',' + gr.company.name;
			//gs.logError('response: ' + ans);
			return ans;
		}
		return 'failed';
	},

	getRevenueCode: function() {
		var revenueString = [];
		var revenueCode = this.getParameter('sysparm_revenue');
		var gr = new GlideRecord('x_soud2_manual_bil_revenue_unit_approvers');
		if (gr.get(revenueCode)) {
			revenueString = gr.revenue_unit.u_code;
			revenueString = revenueString + ',' + gr.revenue_unit.u_revenue_unit;
			revenueString = revenueString + ',' + gr.revenue_unit.u_revenue_unit.u_dept_unit;
			revenueString = revenueString + ',' + gr.revenue_unit.u_rate_value;
			return revenueString;
		}

		 		return 'failed';
	},
	type: 'ManualBilling'
});
