var qry = 'document_id=' + current.sys_id + '^state=rejected';
var grA = new GlideRecord('sysapproval_approver');
grA.addEncodedQuery(qry);
grA.query();

while (grA.next()) {
    qry = 'u_billing_adjustment=' + current.sys_id + '^u_ru_approver.u_approversLIKE' + grA.approver;
    var gr = new GlideRecord('x_soud2_manual_bil_adjustment_details');
    gr.addEncodedQuery(qry);
    gr.addQuery();
    gr.query();

    gr.setValue('approval', 'rejected');
    gr.setValue('state', -5);
    gr.updateMultiple();
}

