var gr = new GlideRecord('x_soud2_manual_bil_adjustment_details');
gr.addQuery('parent',current.sys_id);
gr.query();

gs.addInfoMessage('count: ' + gr.getRowCount());

while (gr.next()) {
	gs.addInfoMessage('number: ' + gr.number);
	gr.state = 1;
	gr.approval = 'requested';
	gr.setWorkflow(false);
	gr.update();
}

