function ifScript() {
	var gr = new GlideRecord('x_soud2_manual_bil_adjustment_details');
	gr.addQuery('parent',current.sys_id);
	gr.addQuery('state','!=',2);
	gr.addQuery('state','!=',55);
	gr.query();
	
	if (gr.hasNext()) {
		return 'no';
	} else {
		return 'yes';
	}
}

